import axios from 'axios';

export const health = async function() {
  try {
    const response = await axios.get(`https://private-bbbe9-blissrecruitmentapi.apiary-mock.com/health`);
    return response.data;
  } catch (error) {
    console.log('There was an error in async list load service');
    throw error;
  }
};

export const list = async function(search) {
  try {
    if(search.split("=")[0] === "filter"){
      const response = await axios.get(`https://private-bbbe9-blissrecruitmentapi.apiary-mock.com/questions?${search}&limit=10&offset=1`);
      return response.data;
    } else if (typeof search.split("=")[0] === number){
      const response = await axios.get(`https://private-bbbe9-blissrecruitmentapi.apiary-mock.com/questions?${search}`);
      return response.data;
    } else {
      const response = await axios.get(`https://private-bbbe9-blissrecruitmentapi.apiary-mock.com/questions?&limit=10&offset=1`);
      return response.data;
    }
  } catch (error) {
    console.log('There was an error in async list load service');
    throw error;
  }
};
