import React, { Component } from "react";
import { Link } from "react-router-dom";
import { health as healthCheck } from "../../Services/api";
import { list as listQuestions } from "../../Services/api";
import Question from "../../Components/Single";

class Home extends Component {
  constructor() {
    super();
    this.state = {
      serverChecking: true,
      serverHealthy: false,
      questions: [],
      question: [],
      search: "",
      multiple: true,
      single: false,
    };
    this.updateSearch = this.updateSearch.bind(this);
  }

  async componentDidMount() {
    const search = this.props.location.search
      .split("")
      .slice(1)
      .join("");
      console.log(search)
    this.setState({
      search: search
    });
    try {
      const health = await healthCheck();
      if (health.status === "OK") {
        const list = await listQuestions(this.state.search);
        const listKeysAdded = list.map(val => {
          val.key = val.id;
          return val;
        });
        this.setState({
          serverChecking: false,
          serverHealthy: true,
          questions: listKeysAdded
        });
      } else {
        this.setState({
          serverChecking: false
        });
      }
    } catch (error) {
      console.log(error);
      console.log("Error in service.");
    }
  }

  async updateSearch(id) {
    const search = "question_id=" + id.toString();
    this.setState({
      search: search
    });
    try {
      const list = await listQuestions(this.state.search);
      const listKeysAdded = list.map(val => {
        val.key = val.id;
        return val;
      });
      this.setState({
        questions: listKeysAdded
      });
      console.log("got the new questions")
    } catch (error) {
      console.log(error);
      console.log("Error in service.");
    }
  }

  render() {

    const questions = this.state.questions;
    return (
      <div>
        <h1>list</h1>
        {this.state.serverChecking && <h3>Checking Server Health</h3>}
        {this.state.serverHealthy && (
          <div>
            {questions.map(val => {
              return (
                <div>
                  <img src={val.thumb_url} />
                  <p>
                    {val.id}. {val.question}
                  </p>
                  <button onClick={() => this.updateSearch(val.id)}>
                    <Link to={`/questions?question_id=${val.id}`}>
                      See details
                    </Link>
                  </button>
                </div>
              );
            })}
          </div>
        )}
      </div>
    );
  }
}

export default Home;
